import React,{useState,useEffect} from 'react'
import Navbar from '../navbar'
import Card from '@material-ui/core/Card';
import Container from '@material-ui/core/Container';
import Button from '@material-ui/core/Button';
import { createTheme ,ThemeProvider } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import './sign.css'

export default () => {
    const theme = createTheme({
        palette: {
          primary: green,
        },
      });
      const [data, setData] = useState({email:'', password:''})
      const [formdata, setFormData] = useState('')
      console.log(data)
      const FormHandler = (e) => {
        setData({...data , [e.target.name]:e.target.value}) 
      }
      const SubmitFormHandler = (e) => {
        setFormData({...formdata , data}) 
        console.log(formdata)
      }
    return (
        <div className='whole_sign'>
            <Navbar/>
            <Container>
              <div className='whole_container'>

                    <Card  className='whole_card'> 
                        <form>
                            <div className='form_row'>
                                <label className='form_lable' htmlFor="email">Email</label>
                                <input name='email' type="text" onChange={FormHandler}/>
                            </div>
                            <div className='form_row'>
                                <label className='form_lable' htmlFor="password">Password</label>
                                <input name='password' type="password" onChange={FormHandler}/>
                            </div>

                        </form>
                        <ThemeProvider theme={theme}>
                                <Button variant="contained" color="primary" onSubmit={SubmitFormHandler} >
                                        Submit
                                </Button>
                            </ThemeProvider>
                    </Card >
              </div>
            </Container>
        </div>

    )
}
