import React,{useState,useEffect} from 'react'
import Navbar from '../navbar'
import Card from '@material-ui/core/Card';
import Container from '@material-ui/core/Container';
import Button from '@material-ui/core/Button';
import { createTheme ,ThemeProvider } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import './sign.css'

export default () => {
    const theme = createTheme({
        palette: {
          primary: green,
        },
      });
      const [data, setData] = useState('')
      const FormHandler = (e) => {
        setData(e.target.value)
        console.log(data)
      }
    return (
        <div className='whole_sign'>
            <Navbar/>
            <Container>
              <div className='whole_container'>

                    <Card  className='whole_card'> 
                        <form>
                            <div className='form_row'>
                                <label className='form_lable' htmlFor="email">Email</label>
                                <input name='email' type="text" value={data} onChange={FormHandler}/>
                            </div>
                            <div className='form_row'>
                                <label className='form_lable' htmlFor="password">Password</label>
                                <input name='password' type="password"value={data} onChange={FormHandler}/>
                            </div>

                            <ThemeProvider theme={theme}>
                                <Button variant="contained" color="primary" >
                                        Submit
                                </Button>
                            </ThemeProvider>

                        </form>
                    </Card >
              </div>
            </Container>
        </div>

    )
}
